mapper.select:
SELECT *
FROM `LIKE`
WHERE USER_ID = #{userId}
AND REFERENCE_TYPE = #{referenceType}
AND REFERENCE_ID = #{referenceId}

mapper.delete:
DELETE FROM `LIKE`
WHERE USER_ID = #{userId}
AND REFERENCE_TYPE = #{referenceType}
AND REFERENCE_ID = #{referenceId}

DELETE FROM `LIKE`
WHERE REFERENCE_TYPE = #{referenceType}
AND REFERENCE_ID = #{referenceId}

mapper.select:
SELECT REFERENCE_ID
FROM `LIKE`
WHERE USER_ID = #{userId}
AND REFERENCE_TYPE = #{referenceType}
AND REFERENCE_ID IN

    #{refId}

SELECT COUNT(*)
FROM `LIKE`
WHERE REFERENCE_TYPE = #{referenceType}
AND REFERENCE_ID = #{referenceId}
